/* @flow */

import Vue from './web-runtime'
import config from 'core/config'
import { query } from 'web/util/index'
import { warn, cached } from 'core/util/index'
import { mark, measure } from 'core/util/perf'
import { shouldDecodeNewlines } from 'web/util/compat'
import { compileToFunctions } from 'web/compiler/index'

const idToTemplate = cached(id => {
  const el = query(id)
  return el && el.innerHTML
})
// 1-2-3-1
// mount 临时存储Vue.prototype.$mount原值
const mount = Vue.prototype.$mount
Vue.prototype.$mount = function (
  el?: string | Element,
  hydrating?: boolean
): Component {
  el = el && query(el)

  /* istanbul ignore if */
  if (el === document.body || el === document.documentElement) {
    process.env.NODE_ENV !== 'production' && warn(
      `Do not mount Vue to <html> or <body> - mount to normal elements instead.`
    )
    return this
  }
  const options = this.$options
  // 1-2-3-2
  // resolve template/el and convert to render function
  // 如果没有render方法
  if (!options.render) {
    let template = options.template
    if (template) { // 1-2-3-3 如果有template， 获取template参数对应的HTML作为模板
      if (typeof template === 'string') {
        if (template.charAt(0) === '#') {
          template = idToTemplate(template)
          /* istanbul ignore if */
          if (process.env.NODE_ENV !== 'production' && !template) {
            warn(
              `Template element not found or is empty: ${options.template}`,
              this
            )
          }
        }
      } else if (template.nodeType) {
        template = template.innerHTML
      } else {
        if (process.env.NODE_ENV !== 'production') {
          warn('invalid template option:' + template, this)
        }
        return this
      }
    } else if (el) { // 1-2-3-4 如果没有template, 且存在el，则获取el的outerHTML作为模板
      template = getOuterHTML(el)
    }
    if (template) { // 1-2-3-5 如果获取到了模板，则将模板转化为render方法
      /* istanbul ignore if */
      if (process.env.NODE_ENV !== 'production' && config.performance && mark) {
        mark('compile')
      }
      // 1-2-3-7 compileToFunctions方法会运行compile将template解析为多个render方法，也就是AST render
      // 定义在 src/platforms/web/compiler/index.js
      const { render, staticRenderFns } = compileToFunctions(template, {
        shouldDecodeNewlines,
        delimiters: options.delimiters
      }, this)
      options.render = render
      options.staticRenderFns = staticRenderFns

      /* istanbul ignore if */
      if (process.env.NODE_ENV !== 'production' && config.performance && mark) {
        mark('compile end')
        measure(`${this._name} compile`, 'compile', 'compile end')
      }
    }
  }
  // 1-2-3-6 ./web-runtime.js
  // 如果有render方法，直接运行mount; Vue 2.0中的模板有三种引用写法：el, template, render(JSX)。其中的优先级是render > template > el
  // 会调用./web-runtime.js 中的Vue.prototype.$mount方法
  return mount.call(this, el, hydrating)
}

/**
 * Get outerHTML of elements, taking care
 * of SVG elements in IE as well.
 */
function getOuterHTML (el: Element): string {
  if (el.outerHTML) {
    return el.outerHTML
  } else {
    const container = document.createElement('div')
    container.appendChild(el.cloneNode(true))
    return container.innerHTML
  }
}

Vue.compile = compileToFunctions

export default Vue
